import { ParserOptions } from '@babel/core';

interface Options {
    plugins?: ParserOptions["plugins"];
    filter?: {
        include?: string | RegExp | Array<string | RegExp>;
        exclude?: string | RegExp | Array<string | RegExp>;
    };
    rsc?: boolean;
    dev?: "debug" | boolean;
    legacyHmr?: boolean;
    apiKey?: string;
    measureBundleSize?: boolean;
    ingest?: {
        host?: string;
        port?: number;
        ignoreMatching?: boolean;
    };
    framework?: string;
}
interface BabelOptions extends Options {
    source?: string;
    isJSX?: boolean;
    filename?: string;
}

export type { BabelOptions as B, Options as O };
